import React, { Component } from "react";

export default class SignIn extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: "",
      password: "",
      isExistUser: true,
    };
  }

  handleChangeUsername = (event) => {
    this.setState({
      username: event.target.value,
    });
  };

  handleChangePassword = (event) => {
    this.setState({
      password: event.target.value,
    });
  };

  signInUser = async (e) => {
    e.preventDefault();
    const url =
      this.props.baseUrl +
      "/users/" +
      (this.state.isExistUser ? "signin" : "signup");

    try {
      const response = await fetch(url, {
        method: "POST",
        body: JSON.stringify({
          username: this.state.username,
          password: this.state.password,
        }),
        headers: {
          "Content-Type": "application/json",
        },
        credentials: "include",
      });

      if (response.status === 200) {
        this.setState({
          username: "",
          password: "",
        });

        this.props.isLoggedIn(true);
      }
    } catch (err) {
      console.log("Error => ", err);
    }
  };

  handleLoginMode = () => {
    this.setState({ isExistUser: !this.state.isExistUser });
  };

  render() {
    return (
      <>
        <form onSubmit={this.signInUser}>
          <strong>{this.state.isExistUser ? "Sign In" : "Sign Up"}</strong>
          <label htmlFor="name">Username:</label>
          <input
            type="text"
            onChange={(e) => this.handleChangeUsername(e)}
            value={this.state.username}
          />
          <label htmlFor="name">Password:</label>
          <input
            type="password"
            onChange={(e) => this.handleChangePassword(e)}
            value={this.state.password}
          />
          <input
            type="submit"
            value={this.state.isExistUser ? "Sign In" : "Sign Up"}
          />
          <button type="button" onClick={this.handleLoginMode}>
            {this.state.isExistUser
              ? "Create new account"
              : "Login with existing account"}
          </button>
        </form>
      </>
    );
  }
}
